import styled from "styled-components"

export const Container = styled.div`
    pad: 25px 0;
    border-bottom: 1px solid #16195C;

    h1 {
        margin: 0;
        padding: 0;
        font-size: 28px;
    }
    p {
        font-size: 14px;
        color: #B8B8D4;
    }
`;
